con:configuration.scriptText:
def itemCount = context.expand('${#Project#ItemCount}')
import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.responseContentAsXml )

def cart_SubTotal = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:SubTotal" )
def cart_Total = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:Total" )
def cart_Tax = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:TotalTax" )

assert cart_SubTotal != null

log.info "Cart Price === SubTotal ="+cart_SubTotal+" & Total ="+cart_Total+" & Tax ="+cart_Tax
log.info "Item Prices ==="
for (int i=0; i<Integer.parseInt(itemCount); i++){	
	def price = context.expand('${#Project#ExtendedPrice'+i+'}')
	def productClass = context.expand('${#Project#productClass'+i+'}')
	def item_Tax = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem[*:ExtendedPrice='"+price+"' and *:Product/@productClass='"+productClass+"']/*:TotalTax" )
//	def item_Class = holder.getNodeValue( "//*:VertexEnvelope[1]/*:QuotationResponse[1]/*:LineItem[1]/*:Product[1]/@productClass" )
	log.info "Tax for item #"+i+ " of Price "+price+ " and type "+productClass+" is " +item_Tax
}
log.info "******************************************************************************************"

def itemCount = context.expand('${#Project#ItemCount}')
import com.eviware.soapui.support.XmlHolder
def holder = new XmlHolder( messageExchange.responseContentAsXml )

for (int i=0; i<Integer.parseInt(itemCount); i++){	
	def price = context.expand('${#Project#ExtendedPrice'+i+'}')
	def item_Tax = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem[*:ExtendedPrice='"+price+"']/*:TotalTax" )
	log.info "Tax for item #"+i+ " of Price = "+price+ " is " +item_Tax
}

def itemCount = context.expand('${#Project#ItemCount}')
def Company = context.expand('${#Project#Company}')
def City = context.expand('${#Project#City}')
def State = context.expand('${#Project#State}')
def Zip = context.expand('${#Project#Zip}')
def Country = context.expand('${#Project#Country}')

import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.responseContentAsXml )
def Company_act = holder.getNodeValue( "//*:VertexEnvelope/*:QuotationResponse/*:Seller/*:Company" )
def City_act = holder.getNodeValues( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem/*:Customer/*:Destination/*:City" )
def State_act = holder.getNodeValues( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem/*:Customer/*:Destination/*:MainDivision" )
def Zip_act = holder.getNodeValues( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem/*:Customer/*:Destination/*:PostalCode" )
def Country_act = holder.getNodeValues( "//*:VertexEnvelope/*:QuotationResponse/*:LineItem/*:Customer/*:Destination/*:Country" )

assert Company == Company_act
for (int i=0; i<Integer.parseInt(itemCount); i++){
	assert City.equals(City_act[i])
	assert State.equals(State_act[i])
	assert Zip.equals(Zip_act[i])
	assert Country.equals(Country_act[i])
}

con:soapui-project.con:beforeRunScript:
def brand = context.expand('${#Project#brand}') //HT/TR/BL

TimeZone.setDefault(TimeZone.getTimeZone('UTC'))
def now = new Date()
//log.info now.format("yyyy-MM-dd HH:mm")
def timenow = now.format("yyyy-MM-dd HH:mm")
project.setPropertyValue( "timenow", timenow)

if(brand.equals('HT')){
	project.setPropertyValue( "site", 'hottopic')
	project.setPropertyValue( "Company", '01')
	project.setPropertyValue( "TrustedId", '$tr.Jib7eNBD')
}
else if(brand.equals('TR')){
	project.setPropertyValue( "site", 'torrid')
	project.setPropertyValue( "Company", '05')
	project.setPropertyValue( "TrustedId", '$tr.Ez7Cy7oG')
}
else if(brand.equals('BL')){
	project.setPropertyValue( "site", 'hottopic')
	project.setPropertyValue( "Company", '04')
	project.setPropertyValue( "TrustedId", '$tr.Jib7eNBD')
}

//log.info "properties set Brand = " +context.expand('${#Project#site}')+", TrustedId = "+ context.expand('${#Project#TrustedId}')

